SELECT c.id as "Curso Id", c.fullname as "Nome Curso", fedv.completed as "Id Pesquisa de Satisfação", fedi.name as "Questão", fedv.value as "Resposta Índice", CASE WHEN(fedv.value = '1') THEN '1. Muito bom' WHEN(fedv.value = '2') THEN '2.Bom' WHEN(fedv.value = '3') THEN '3.Razoável' WHEN(fedv.value = '4') THEN '4.Ruim' WHEN(fedv.value = '5') THEN '5.Muito Ruim' ELSE fedv.value END as Resposta FROM mdl_feedback as fed INNER JOIN mdl_feedback_item as fedi on fed.id = fedi.feedback INNER JOIN mdl_feedback_value as fedv on fedi.id = fedv. item INNER JOIN mdl_course as c on fed.course = c.id INNER JOIN mdl_course_categories as cc on cc.id = c.category WHERE cc."path" like '%752%' ORDER BY c.id, fedv.completed, fedi.name
