select date(to_timestamp(mm.timecreated)) as dia, concat(mu2.firstname, ' ', mu2.lastname) as recebidos, Count(mu.id) as total from mdl_messages mm inner join mdl_message_conversation_members mmcm on mmcm.conversationid = mm.conversationid inner join mdl_user mu on mu.id = mm.useridfrom inner join mdl_user mu2 on mu2.id = mmcm.userid and mm.useridfrom != mmcm.userid group by mm.timecreated, recebidos order by total desc
